/* browser-form.js
    
    Recipe browser form component.
*/

// Dependencies.
const { Component } = require("react"),
    dispatch = require("app/front/dispatch.js");

/* Recipe browser form component.
*/
class BrowserForm extends Component {
    //
    // STATIC PROPERTIES
    //
    
    //
    // PROPERTIES
    //
    
    /* Constructor for BrowserForm.
    */
    constructor(props) {
        super(props);
    }
    
    /* Mount handler.
    */
    componentDidMount() {
        this.setState({
            data: {}
        });
    }
    
    /* Destructor.
    */
    componentWillUnmount() {
        this.state.data = null;
    }
    
    /* Update handler.
    */
    componentDidUpdate() {
    }
    
    //
    // STATIC METHODS
    //
    
    //
    // METHODS
    //
    
    /* Form button click handler.
        
        e - Click event.
    */
    onClick = (e) => {
        e.preventDefault();
        
        let btn = e.target;
        
        dispatch(btn, "app-open-file", {
            bubbles: true
        });
        
        btn.blur();
    };
    
    /* Renders the browser form.
    */
    render() {
        return (
            <form id="browser-form" className="grid">
                <div className="col-12">
                    <input
                        id="receipe-file-button"
                        type="button"
                        accept=".recipe-pack"
                        value="Open Recipes&hellip;"
                        onClick={this.onClick}
                    />
                </div>
            </form>
        );
    }
}

module.exports = BrowserForm;
