/* browser-form.js
    
    Recipe browser form component.
*/

// Dependencies.
const { Component } = require("react");

/* Recipe browser form component.
*/
class BrowserForm extends Component {
    //
    // STATIC PROPERTIES
    //
    
    //
    // PROPERTIES
    //
    
    // File input element.
    _fileInput = null;
    
    /* Constructor for BrowserForm.
    */
    constructor(props) {
        super(props);
    }
    
    /* Mount handler.
    */
    componentDidMount() {
        this.setState({
            data: {}
        });
    }
    
    /* Destructor.
    */
    componentWillUnmount() {
        this.state.data = null;
    }
    
    /* Update handler.
    */
    componentDidUpdate() {
    }
    
    //
    // STATIC METHODS
    //
    
    //
    // METHODS
    //
    
    /* Form button click handler.
        
        e - Click event.
    */
    onClick = (e) => {
        e.preventDefault();
        
        if (null === this._fileInput) {
            return;
        }
        
        this._fileInput.click();
    };
    
    /* File load change handler.
        
        e - Change event.
    */
    onFileLoad = (e) => {
    };
    
    /* Renders the browser form.
    */
    render() {
        return (
            <form id="browser-form" className="grid">
                <div className="col-12">
                    <input
                        id="receipe-file-button"
                        type="button"
                        value="Open Recipes&hellip;"
                        onClick={this.onClick}
                    />
                    <input
                        id="recipe-file"
                        type="file"
                        className="hidden-file-input"
                        ref={(r) => { this._fileInput = r; }}
                        onChange={this.onFileLoad}
                    />
                </div>
            </form>
        );
    }
}

module.exports = BrowserForm;
