/* recipe-list-item.js
    
    Recipe list item component.
*/

// Dependencies.
const { Component } = require("react"),
    Recipe = require("app/front/recipe.js");

/* Recipe component.
*/
class RecipeListItem extends Component {
    //
    // STATIC PROPERTIES
    //
    
    //
    // PROPERTIES
    //
    
    /* Constructor for RecipeListItem.
    */
    constructor(props) {
        super(props);
        
        this.state = {
            recipe: new Recipe(props)
        };
    }
    
    /* Mount handler.
    */
    componentDidMount() {
        this.recipe = this.props;
    }
    
    /* Destructor.
    */
    componentWillUnmount() {
        this.recipe.destroy();
    }
    
    //
    // STATIC METHODS
    //
    
    //
    // METHODS
    //
    
    /* Getter for state.recipe.
        
        Returns the recipe data object for the list item.
    */
    get recipe() {
        return this.state.recipe;
    }
    
    /* Setter for state.recipe.
        
        data - Raw recipe data.
    */
    set recipe(data) {
        this.setState({
            recipe: new Recipe(data)
        })
    }
    
    /* Renders the recipe.
    */
    render() {
        let recipe = this.state.recipe;
        
        return (
            <li key={recipe.id} className="row">
                <div className="name col-8">
                    <span className="name">{recipe.name}</span>
                </div>
                <div className="controls col-4">
                    <div className="delete">X</div>
                </div>
            </li>
        );
    }
}

module.exports = RecipeListItem;
