/* recipe-list.js
    
    Recipe list object.
*/

// Dependencies.
const { Component } = require("react"),
    LinkedList = require("app/all/linked-list.js"),
    RecipeListItem = require("app/front/browser/components/" +
        "recipe-list-item.js");

/* Recipe list component.
*/
class RecipeList extends Component {
    //
    // STATIC PROPERTIES
    //
    
    //
    // PROPERTIES
    //
    
    //
    // STATIC METHODS
    //
    
    //
    // METHODS
    //
    
    /* Renders the recipe list.
    */
    render() {
        let listItems = [],
            timestamp = Date.now(),
            index = 0;
        
        this.props.recipes.each((recipe, node) => {
            listItems.push(
                <RecipeListItem
                    key={`${timestamp}-${index}`}
                    name={recipe.name}
                    title={recipe.title}
                    node={node}
                />
            );
            
            ++index;
        });
        
        return (
            <ol className="recipe-list grid">
                {listItems}
            </ol>
        );
    }
}

module.exports = RecipeList;
