/* recipe-list.js
    
    Recipe list object.
*/

// Dependencies.
const { Component } = require("react"),
    RecipeListItem = require("app/front/browser/components/" +
        "recipe-list-item.js");

/* Recipe list component.
*/
class RecipeList extends Component {
    //
    // STATIC PROPERTIES
    //
    
    //
    // PROPERTIES
    //
    
    //
    // STATIC METHODS
    //
    
    //
    // METHODS
    //
    
    /* Renders the recipe list.
    */
    render() {
        let props = this.props,
            listItems = [];
        
        this.props.recipes.each((recipe, node) => {
            listItems.push(
                <RecipeListItem
                    key={recipe.id}
                    id={recipe.id}
                    name={recipe.name}
                    selected={props.selected === recipe.id}
                />
            );
        });
        
        return (
            <ol className="recipe-list">
                {listItems}
            </ol>
        );
    }
}

module.exports = RecipeList;
